package com.util;

import java.util.HashMap;
import java.util.Map;

import org.omg.PortableInterceptor.ServerRequestInfo;

import com.google.gson.Gson;
import com.model.College;
import com.model.Course;
import com.model.CourseTime;
import com.model.Grade;
import com.model.Major;
import com.model.Student;
import com.model.Teacher;

public class JsonUtil {
	private static Gson gson;
	static{
		gson=new Gson();
	}

	public static String studentToString(Student student){
		String reString=null;
		reString=gson.toJson(student);
		return reString;
	}
	
	public static Student StringToStudent(String studentJson){
		Student student=null;
		student=gson.fromJson(studentJson,Student.class);
		return student;
	}
	
	
	public static String teacherToString(Teacher teacher){
		String reString=null;
		reString=gson.toJson(teacher);
		return reString;
	}
	
	public  static Teacher stringToTeacher(String teacherJson){
		Teacher teacher=null;
		teacher=gson.fromJson(teacherJson, Teacher.class);
		return teacher;
	}
	
	
	public static String courseToString(Course course){
		String reString=null;
		reString=gson.toJson(course);
		return reString;
	}
	
	public  static Course stringToCourse(String courseJson){
		Course course=null;
		course=gson.fromJson(courseJson, Course.class);
		return course;
	}
	
	
	public static String collegeToString(College college){
		String reString=null;
		
		reString=gson.toJson(college);
		return reString;
		
	}
	
	
	public static College stringToCollege(String collegeJson){
		 College college =null;
		 college=gson.fromJson(collegeJson, College.class);
		 return college;
		
	}
	
	
	public static String gradeToString(Grade grade){
		String reString=null;
		
		reString=gson.toJson(grade);
		return reString;
	}
	
	
	
	public static Grade stringToGrade(String gradeJson){
		Grade  grade=null;
		
		grade=gson.fromJson(gradeJson, Grade.class);
		return grade;
		
	}
	
	
	public static String majorToString(Major major){
		String reString=null;
		reString=gson.toJson(major);
		return reString;
		
	}
	
	
	public static Major stringToMajor(String majorJson){
		
		Major major=null;
		major=gson.fromJson(majorJson, Major.class);
		return major;
	}
	
	public static String courseTimeToString(CourseTime courseTime){
		
		String reString=null;
		reString=gson.toJson(courseTime);
		
		return reString;
		
		
	}
	
	public static CourseTime stringToCourseTime(String courseTimeJson ){
		
		CourseTime courseTime=null;
		courseTime=gson.fromJson(courseTimeJson, CourseTime.class);
		return courseTime;
		
	}
	
	//将装有选课信息的map转化为字符串
    //顺序为：id,学号，课程id,教师工号，成绩，选课学期
	public static String courseMapToString(Map<String ,String> courseMap){
		String  str=null;
		StringBuffer temp=new StringBuffer();
		str=courseMap.get("id");
		temp.append(str+",");
		str=courseMap.get("stu_code");
		temp.append(str+",");
		str=courseMap.get("cou_code");
		temp.append(str);
		str=courseMap.get("tea_code");
		temp.append(str+",");
		str=courseMap.get("score");
		temp.append(str+",");
		str=courseMap.get("term");
		temp.append(str);
		return temp.toString();
	}
	
	//将从文件中读出来的选课信息组装成map
	//顺序为：id,学号，课程id,教师工号，成绩，选课学期
	public static Map<String, String> stringToCourseMap(String courseString){
		Map<String, String> courseMap=new HashMap<>();
		String temps[]=courseString.split(",");
		courseMap.put("id", temps[0]);
		courseMap.put("stu_code", temps[1]);
		courseMap.put("cou_code", temps[2]);
		courseMap.put("tea_code", temps[3]);
		courseMap.put("score", temps[4]);
		courseMap.put("term", temps[5]);
		
		
		return courseMap;
	}
	
	public static void main(String arsg[]){
		Map<String, String> temp=new HashMap<>();
		String string="12,234,23,2342,44,3";
		temp=JsonUtil.stringToCourseMap(string);
		System.out.println(temp.get("stu_code"));
		
	}
		
}

	                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                 

